	<?php 
		if (isset($_POST["id"])&&$_POST["id"]!=null){
			echo("<h2>Going to stop scan: ".htmlentities($_POST["id"])." - ".htmlentities($_POST["siteName"])."</h2>");
			$STOP=true;
		}else{
			$STOP=false;
		}
		
		if($STOP===true){
			curl_setopt_array($curl, array(
			  CURLOPT_URL => "https://".$API_IP.":".$API_PORT."/api/3/scans/".$_POST["id"]."/stop",
			  CURLOPT_CUSTOMREQUEST => "POST",
			));

			$STOP_response = curl_exec($curl);
			$err = curl_error($curl);

			curl_close($curl);

			if ($err) {
			  die("cURL Error #:" . $err);
			} else {
			  $jsonSTOP = json_decode($STOP_response, true);
			  //print_r($jsonSTOP);
			  if(isset($jsonSTOP["status"])||isset($jsonSTOP["message"])){
				echo("<p><b>Error CODE: </b>".$jsonSTOP["status"]."</p><p><b>Message: </b>".$jsonSTOP["message"]."</p><br><a href='".$_SERVER['PHP_SELF']."'>Go Back</a>");
			  }else{
				  echo("
				  <h2>Wait for the timeout to complete, the page will automatically refresh</h2><center>
				  <img src='data:image/gif;base64,'/></center>
				<script>setTimeout('location.href = '".$_SERVER['PHP_SELF']."';',1500);</script>
				  ");
			  }
			  die();
			}
		$STOP=false;
		}
	?>
